package basics;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4;
		int var = 6;//default value is zero
		System.out.println(var==a);
		var+=a;// var= var+a;
		System.out.println(var);
		var*=a;// var =var*a;
		System.out.println(var);
		var/=a;// var=var/a;
		System.out.println(var);
		var%=a;//var=var%a;
		System.out.println(var);
	}

}
